mapper.select:
SELECT COUNT(*)
  FROM user
 WHERE account_id = #{accountId}

SELECT COUNT(*)
  FROM user
 WHERE nick_name = #{nickName}

SELECT COUNT(*)
  FROM user
 WHERE user_email = #{userEmail}

SELECT account_id, user_password
  FROM user
 WHERE account_id = #{accountId} AND user_password = #{userPassword}

SELECT user_id, account_id, user_name, nick_name, user_password, user_email, user_phone, created_at, updated_at, point
  FROM user
 WHERE account_id = #{accountId}

SELECT user_id, account_id,user_name, nick_name
FROM user
WHERE account_id = #{account_id}
