mapper.select:
SELECT wishlist_id, user_id, product_id
FROM wishlist
WHERE user_id = #{userId}

mapper.delete:
DELETE FROM wishlist
WHERE user_id = #{userId}
AND product_id = #{productId}

mapper.select:
SELECT product_id, product_name, product_price, product_description
FROM product
WHERE product_id = #{productId}

SELECT image_id, product_id, image_url, image_key, is_thumbnail
FROM product_image
WHERE product_id = #{productId} AND is_thumbnail = 1;
